{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "properties": {
    "userIdent": {
      "type": "string",
      "description": "\"Custom ID\" in UCRM, not to be confused with entity \"ID\" used in URL"
    },
    "organizationId": {
      "type": "number",
      "description": "If not specified, default organization will be used\n\nSee [Retrieve Organizations](#reference/organizations/organizations/get)"
    },
    "isLead": {
      "type": "boolean",
      "description": "if true, this is a lead, otherwise active client",
      "default": false
    },
    "clientType": {
      "type": "number",
      "enum": [
        1,
        2
      ],
      "default": 1
    },
    "companyName": {
      "type": "string",
      "description": "Required in case of type **Company**"
    },
    "companyRegistrationNumber": {
      "type": "string"
    },
    "companyTaxId": {
      "type": "string"
    },
    "companyWebsite": {
      "type": "string"
    },
    "companyContactFirstName": {
      "type": "string"
    },
    "companyContactLastName": {
      "type": "string"
    },
    "firstName": {
      "type": "string",
      "description": "Required in case of type **Residential**"
    },
    "lastName": {
      "type": "string",
      "description": "Required in case of type **Residential**"
    },
    "street1": {
      "type": "string"
    },
    "street2": {
      "type": "string"
    },
    "city": {
      "type": "string"
    },
    "countryId": {
      "type": "number",
      "description": "If not specified, taken from default organization\n\nSee [Retrieve Countries](#reference/general/countries/get)"
    },
    "stateId": {
      "type": "number",
      "description": "Applicable in case of **United States** and **Canada** only. If not specified, taken from default organization\n\nSee [Retrieve States](#reference/general/countriesidstates/get)"
    },
    "zipCode": {
      "type": "string"
    },
    "invoiceAddressSameAsContact": {
      "type": "boolean",
      "default": false
    },
    "invoiceStreet1": {
      "type": "string"
    },
    "invoiceStreet2": {
      "type": "string"
    },
    "invoiceCity": {
      "type": "string"
    },
    "invoiceCountryId": {
      "type": "number",
      "description": "See [Retrieve Countries](#reference/general/countries/get)"
    },
    "invoiceStateId": {
      "type": "number",
      "description": "Applicable in case of **United States** and **Canada** only\n\nSee [Retrieve States](#reference/general/countriesidstates/get)"
    },
    "invoiceZipCode": {
      "type": "string"
    },
    "sendInvoiceByPost": {
      "type": "boolean",
      "description": "If `null`, system default is used\n\nMark client's invoices as to be sent by post",
      "default": false
    },
    "invoiceMaturityDays": {
      "type": "number",
      "description": "If `null`, system default is used",
      "default": 14
    },
    "stopServiceDue": {
      "type": "boolean",
      "description": "If `null`, system default is used\n\nSuspend client's service in case of overdue invoice",
      "default": true
    },
    "stopServiceDueDays": {
      "type": "number",
      "description": "If `null`, system default is used\n\nNumber of days for which suspend is deferred\nFor example, if 3 days are set and invoice due date is 17th March suspend will start from 20th March",
      "default": 0
    },
    "tax1Id": {
      "type": "number",
      "description": "Will be added by default to each client's taxable services and products\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "tax2Id": {
      "type": "number",
      "description": "Will be added by default to each client's taxable services and products\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "tax3Id": {
      "type": "number",
      "description": "Will be added by default to each client's taxable services and products\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "registrationDate": {
      "type": "string",
      "description": "Date string in ISO 8601 format. If not specified, current date will be used"
    },
    "previousIsp": {
      "type": "string"
    },
    "note": {
      "type": "string"
    },
    "username": {
      "type": "string",
      "description": "If null, client zone is disabled"
    },
    "avatarColor": {
      "type": "string",
      "description": "Color in hexadecimal format. If not specified, will be assigned randomly."
    },
    "contacts": {
      "type": "array"
    },
    "attributes": {
      "type": "array"
    }
  }
}

