      "type": "string",
      "description": "If not specified, taken from client"
    },
    "street2": {
      "type": "string",
      "description": "If not specified, taken from client"
    },
    "city": {
      "type": "string",
      "description": "If not specified, taken from client"
    },
    "countryId": {
      "type": "number",
      "description": "If not specified, taken from client\n\nSee [Retrieve Countries](#reference/general/countries/get)"
    },
    "stateId": {
      "type": "number",
      "description": "Applicable in case of **United States** and **Canada** only. If not specified, taken from client\n\nSee [Retrieve States](#reference/general/countriesidstates/get)"
    },
    "zipCode": {
      "type": "string",
      "description": "If not specified, taken from client"
    },
    "addressGpsLat": {
      "type": "number",
      "description": "Latitude of service location"
    },
    "addressGpsLon": {
      "type": "number",
      "description": "Longitude of service location"
    },
    "contractId": {
      "type": "string"
    },
    "contractLengthType": {
      "type": "number",
      "enum": [
        1,
        2
      ],
      "default": 1
    },
    "minimumContractLengthMonths": {
      "type": "number",
      "description": "Minimum contract length in months"
    },
    "contractEndDate": {
      "type": "string",
      "description": "Date string in ISO 8601 format. Required in case of **Close end contract**"
    },
    "discountType": {
      "type": "number",
      "enum": [
        0,
        1,
        2
      ],
      "default": 0
    },
    "discountValue": {
      "type": "number"
    },
    "discountInvoiceLabel": {
      "type": "string"
    },
    "discountFrom": {
      "type": "string",
      "description": "Date string in ISO 8601 format. Must match one of the selected service plan period's start dates"
    },
    "discountTo": {
      "type": "string",
      "description": "Date string in ISO 8601 format. Must match one of the selected service plan period's end dates"
    },
    "taxable": {
      "type": "boolean",
      "default": false
    },
    "tax1Id": {
      "type": "number",
      "description": "Applicable if the service is taxable. If not specified, taken from client\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "tax2Id": {
      "type": "number",
      "description": "Applicable if the service is taxable. If not specified, taken from client\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "tax3Id": {
      "type": "number",
      "description": "Applicable if the service is taxable. If not specified, taken from client\n\nSee [Retrieve Taxes](#reference/taxes/taxes/get)"
    },
    "hasIndividualPrice": {
      "type": "boolean",
      "description": "True if the service price is set individually, false if it's inherited from service plan."
    },
    "totalPrice": {
      "type": "number",
      "description": "Total price of service, including surcharges, discounts and taxes. Price is calculated for current invoicing period and thus accounts for time-limited discounts."
    },
    "servicePlanName": {
      "type": "string"
    },
    "servicePlanPrice": {
      "type": "number"
    },
    "servicePlanPeriod": {
      "type": "number",
      "description": "Pricing period in months"
    },
    "downloadSpeed": {
      "type": "number",
      "description": "Download speed in Mbps"
    },
    "uploadSpeed": {
      "type": "number",
      "description": "Upload speed in Mbps"
    },
    "ipRanges": {
      "type": "array"
    },
    "currencyCode": {
      "type": "string"
    },
    "hasOutage": {
      "type": "boolean"
    }
  },
  "required": [
    "servicePlanId",
    "servicePlanPeriodId"
  ]
}

