{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "properties": {
    "interfaceId": {
      "type": "number",
      "description": "Device/router's interface to which the client's device is connected. This is important for suspend feature\n\nSee [Retrieve Device Interfaces](#reference/device-interfaces/devicesiddevice-interfaces/get)"
    },
    "vendorId": {
      "type": "number",
      "description": "See [Retrieve Vendors](#reference/vendors/vendors/get)"
    },
    "ipRange": {
      "type": "array",
      "description": "Supported formats: Single IP: 192.168.1.20, IP range: 192.168.1.20‑192.168.1.30 or 192.168.1.20‑30, IP range with netmask: 192.168.1.20/26"
    },
    "macAddress": {
      "type": "string",
      "description": "Supported MAC formats are 00:AA:BB:00:11:22, 00‑AA‑BB‑00‑11‑22, 00AABB001122"
    },
    "loginUsername": {
      "type": "string"
    },
    "loginPassword": {
      "type": "string"
    },
    "sshPort": {
      "type": "number",
      "default": 22
    },
    "sendPingNotifications": {
      "type": "boolean",
      "default": false
    },
    "pingNotificationUserId": {
      "type": "number",
      "description": "If not specified, default notification user will be used\n\nSee [Retrieve Admins](#reference/users/usersadmins/get)"
    },
    "createPingStatistics": {
      "type": "boolean",
      "default": true
    },
    "createSignalStatistics": {
      "type": "boolean",
      "default": true
    },
    "qosEnabled": {
      "type": "number",
      "enum": [
        0,
        1,
        2
      ],
      "default": 0
    },
    "qosDeviceIds": {
      "type": "array",
      "description": "Applicable in case of **On other device(s)** only\n\nSee [Retrieve Devices](#reference/devices/devices/get)"
    }
  },
  "required": [
    "interfaceId",
    "vendorId",
    "ipRange"
  ]
}

