  "type": "object",
  "properties": {
    "id": {
      "type": "number"
    },
    "email": {
      "type": "string"
    },
    "username": {
      "type": "string"
    },
    "firstName": {
      "type": "string"
    },
    "lastName": {
      "type": "string"
    },
    "avatarColor": {
      "type": "string",
      "description": "Color in hexadecimal format. If not specified, will be assigned randomly."
    }
  },
  "required": [
    "email",
    "username"
  ]
}

{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "properties": {
    "id": {
      "type": "number"
    },
    "name": {
      "type": "string"
    },
    "registrationNumber": {
      "type": "string"
    },
    "taxId": {
      "type": "string"
    },
    "phone": {
      "type": "string"
    },
    "email": {
      "type": "string",
      "description": "Used as the \"From attribute\" for all the messages sent to clients under this organization"
    },
    "website": {
      "type": "string"
    },
    "street1": {
      "type": "string"
    },
    "street2": {
      "type": "string"
    },
    "city": {
      "type": "string"
    },
    "countryId": {
      "type": "number",
      "description": "See [Retrieve Countries](#reference/general/countries/get)"
    },
    "stateId": {
      "type": "number",
      "description": "Applicable in case of **United States** and **Canada** only. If not specified, taken from default organization\n\nSee [Retrieve States](#reference/general/countriesidstates/get)"
    },
    "zipCode": {
      "type": "string"
    },
    "selected": {
      "type": "boolean",
      "description": "Is preselected while adding new client",
      "default": false
    }
  },
  "required": [
    "name",
    "street1",
    "city",
    "countryId",
    "zipCode"
  ]
}

{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "properties": {
    "name": {
      "type": "string"
    },
    "organizationId": {
      "type": "number",
      "description": "If not specified, default organization will be used"
    },
    "invoiceLabel": {
      "type": "string",
      "description": "If not specified, name will be used"
    },
    "downloadBurst": {
      "type": "number",
      "description": "Download burst in kB"
    },
    "uploadBurst": {
      "type": "number",
      "description": "Upload burst in kB"
    },
    "downloadSpeed": {
      "type": "number",
      "description": "Download speed in Mbps"
    },
    "uploadSpeed": {
      "type": "number",
      "description": "Upload speed in Mbps"
    },
    "dataUsageLimit": {
      "type": "number",
      "description": "Data usage limit in GB"
    },
    "taxable": {
      "type": "boolean",
      "default": false
    },
    "periods": {
      "type": "array",
      "description": "By default 1 month period is enabled"
    }
  },
  "required": [
    "name"
  ]
}

